// src/pages/TourSubmission.js
import React, { useState } from 'react';
import axios from 'axios';

const TourSubmission = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [location, setLocation] = useState('');
  const [category, setCategory] = useState('');
  const [tags, setTags] = useState('');
  const [mediaFile, setMediaFile] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('title', title);
    formData.append('description', description);
    formData.append('location', location);
    formData.append('category', category);
    formData.append('tags', tags);
    formData.append('mediaFile', mediaFile);

    // Submit tour data to the server
    axios
      .post('/api/tours', formData)
      .then((response) => {
        console.log('Tour submitted successfully!', response.data);
        // Reset form fields
        setTitle('');
        setDescription('');
        setLocation('');
        setCategory('');
        setTags('');
        setMediaFile(null);
      })
      .catch((error) => {
        console.error('Error submitting tour:', error);
      });
  };

  return (
    <div>
      <h2>Submit a New Tour</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="location">Location:</label>
          <input
            type="text"
            id="location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="category">Category:</label>
          <input
            type="text"
            id="category"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="tags">Tags (comma-separated):</label>
          <input
            type="text"
            id="tags"
            value={tags}
            onChange={(e) => setTags(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="mediaFile">Media File (360Â° photo or video):</label>
          <input
            type="file"
            id="mediaFile"
            onChange={(e) => setMediaFile(e.target.files[0])}
            required
          />
        </div>
        <button type="submit">Submit Tour</button>
      </form>
    </div>
  );
};

export default TourSubmission;
